import React, { useEffect } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { cardActions } from "./card/reducer";
import Posts from "./components/posts/Posts";

const CardScreen = ({ data, error, isLoading, card, comments }) => {
  console.log("data", data);
  useEffect(() => {
    card();
    comments();
  }, []);
  return (
    <div className="mainDivPost">
      <h4>Posts</h4>
      {data.map(item => {
        return (
          <Posts
            postTitle={item.title}
            postBody={item.body}
            postId={item.id}
            userId={item.userId}
          />
        );
      })}
    </div>
  );
};
const mapStateToProps = ({ card }) => {
  const { data, error, isLoading } = card;
  return { data, error, isLoading };
};
const mapDispatchToProps = dispatch => ({
  ...bindActionCreators(
    {
      ...cardActions.Creators
    },
    dispatch
  )
});

export default connect(mapStateToProps, mapDispatchToProps)(CardScreen);
