import React, { useEffect } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { cardActions } from "./card/reducer";
import Authors from "./components/author/Authors";

const AuthorScreen = ({ authordata, authors }) => {
  console.log("data", data);
  useEffect(() => {
    authors();
  }, []);
  return (
    <div className="mainDivPost">
      <h4>Authors</h4>
      {data.map(item => {
        return (
          <Authors
          authorName={item.name}
          authorContact={item.phone}
          authorEmail={item.email}
          authorWebsite={item.website}
          />
        );
      })}
    </div>
  );
};
const mapStateToProps = ({ author }) => {
  const { data, error, isLoading } = author;
  return { data, error, isLoading };
};
const mapDispatchToProps = dispatch => ({
  ...bindActionCreators(
    {
      ...cardActions.Creators
    },
    dispatch
  )
});

export default connect(mapStateToProps, mapDispatchToProps)(AuthorScreen);
