import { createActions, createReducer } from "reduxsauce";
const INITIAL_STATE = {
  data: [],
  error: "",
  isLoading: false
};
const { Types, Creators } = createActions({
  authors: null,
  authorsSuccess: ["data"],
  authorsFailure:["error"]
});
export const authorActions = {
  Types,
  Creators
};
const author = (state) => {
  return {
    ...state,
    isLoading: true,
    error: ""
  };
};
const authorsSuccess = (state, response) => {
  return {
    ...state,
    data: response.data,
    isLoading: false
  };
};
const authorsFailure = (state, error) => {
  return {
    ...state,
    isLoading: false,
    error
  };
};
export const authorReducer = createReducer(INITIAL_STATE, {
  [Types.AUTHORS]: author,
  [Types.AUTHORS_FAILURE]: authorsSuccess,
  [Types.AUTHORS_FAILURE]: authorsFailure
});
