import React, { useEffect } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { cardActions } from "./card/reducer";


const CardScreen = ({ data, error, isLoading, card }) => {
  console.log("data", data);
  useEffect(() => {
    card();
  }, []);
  return (
    <div>
      {data.map(item => {
        return (
          <posts
            blogTitle={"10 investing strategies to last for a lifetime"}
            author={"Tashi Jain"}
            readTime={11}
            datePosted={"15/10/18"}
            category={"Smart LifeStyle"}
            isBookmarked={true}
            blogImage={
              "https://d2j99f6syf87tg.cloudfront.net/lxme/lxme_web/blog-img-1.jpg"
            }
          />
        );
      })}
    </div>
  );
};
const mapStateToProps = ({ card }) => {
  const { data, error, isLoading } = card;
  return { data, error, isLoading };
};
const mapDispatchToProps = dispatch => ({
  ...bindActionCreators(
    {
      ...cardActions.Creators
    },
    dispatch
  )
});

export default connect(mapStateToProps, mapDispatchToProps)(CardScreen);
