import { all,takeEvery } from "redux-saga/effects";
import {getCardSaga, getCommentsSaga, getAuthorsSaga} from './card/saga';
import {cardActions} from './card/reducer';
export default function* rootSagas(){
    yield all([yield takeEvery(cardActions.Types.CARD,getCardSaga)]);
    yield all([yield takeEvery(cardActions.Types.COMMENTS,getCommentsSaga)]);
    yield all([yield takeEvery(cardActions.Types.AUTHORS,getAuthorsSaga)])
}
