import { createActions, createReducer } from "reduxsauce";
const INITIAL_STATE = {
  data: [],
  error: "",
  isLoading: false
};
const { Types, Creators } = createActions({
  card: null,
  cardSuccess: ["data"],
  cardFailure: ["error"],
  comments: null,
  commentsSuccess: ["data"],
  commentsFailure:["error"]
});
export const cardActions = {
  Types,
  Creators
};
const card = state => {
  return {
    ...state,
    isLoading: true,
    error: ""
  };
};
const cardSuccess = (state, response) => {
  return {
    ...state,
    data: response.data,
    isLoading: false
  };
};
const cardFailure = (state, error) => {
  return {
    ...state,
    isLoading: false,
    error
  };
};
const comments = (state) => {
  return {
    ...state,
    isLoading: true,
    error: ""
  };
};
const commentsSuccess = (state, response) => {
  return {
    ...state,
    data: response.data,
    isLoading: false
  };
};
const commentsFailure = (state, error) => {
  return {
    ...state,
    isLoading: false,
    error
  };
};
const authors = (state) => {
  return {
    ...state,
    isLoading: true,
    error: ""
  };
};
const authorsSuccess = (state, response) => {
  return {
    ...state,
    data: response.data,
    isLoading: false
  };
};
const authorsFailure = (state, error) => {
  return {
    ...state,
    isLoading: false,
    error
  };
};
export const cardReducer = createReducer(INITIAL_STATE, {
  [Types.CARD]: card,
  [Types.CARD_SUCCESS]: cardSuccess,
  [Types.CARD_FAILURE]: cardFailure,
  [Types.COMMENTS]: comments,
  [Types.COMMENTS_FAILURE]: commentsSuccess,
  [Types.COMMENTS_FAILURE]: commentsFailure,
  [Types.AUTHORS]: authors,
  [Types.AUTHORS_FAILURE]: authorsSuccess,
  [Types.AUTHORS_FAILURE]: authorsFailure
});
